import { createReducer, on } from '@ngrx/store';
import * as actions from './ui.actions';
export const initialState = {
  isLoading: false
};

const _uiReducer = createReducer(initialState, on(actions.isLoading, state => Object.assign(Object.assign({}, state), {
  isLoading: true
})), on(actions.stopLoading, state => Object.assign(Object.assign({}, state), {
  isLoading: false
})));

export function uiReducer(state, action) {
  return _uiReducer(state, action);
}
